export class FormularioUtil {
  static mostrarImagem(event, inputImageElement, imagemElement) {
    const file = this.pegarImagemDoInput(inputImageElement);
    var reader = new FileReader();
    var imgtag = imagemElement.nativeElement;

    reader.onload = function (event) {
      imgtag.src = event.target.result;
    };

    reader.readAsDataURL(file);
  }

  static pegarImagemDoInput(imagemElement) {
    const fileList = imagemElement.nativeElement.files;
    return fileList[0];
  }

}
