import { imgDefault } from './imagem-default';
export class UsuarioUtil {
  static pegarFormData(form) {
    const formData = new FormData();
    formData.append('nomeCompleto', form.nomeCompleto);
    formData.append('username', form.username);
    formData.append('password', form.password);
    if (form.imagemPath) formData.append('file', form.imagemPath, form.imagemPath.name);
    return formData;
  }

  static ocutarModal(modal, renderer) {
    renderer.setStyle(modal.nativeElement, 'display', 'none');
  }

  static pegarFormDataAtualizacaoDeInformacoes(atualizarUsuarioDto) {
    const formData = new FormData();
    formData.append('nomeCompleto', atualizarUsuarioDto.nomeCompleto);
    formData.append('username', atualizarUsuarioDto.username);
    if (atualizarUsuarioDto.imagem) formData.append('file', atualizarUsuarioDto.imagem, atualizarUsuarioDto.imagem.name);
    return formData;
  }

  static definirImagemDefault(event) {
    const imagem = event.target;
    imagem.src = imgDefault;
  }

}
