import { ChatUtil } from "../util/chat.util";
export class MensagemDto {
  static mapearFormParaDto(mensagemForm, usernameUsuarioAutenticado) {
    const mensagemDto = new MensagemDto();
    const dataAtual = new Date();
    mensagemDto.dataSemFormatar = new Date(dataAtual.setHours(dataAtual.getHours() + 3));
    mensagemDto.destinatario = mensagemForm.sendTo;
    mensagemDto.emissor = usernameUsuarioAutenticado;
    mensagemDto.mensagem = mensagemForm.mensagem;
    mensagemDto.dataDeEnvio = ChatUtil.formatarData(mensagemDto.dataSemFormatar);
    return mensagemDto;
  }

}
