import { HttpEvent, HttpHandler, HttpInterceptor, HttpRequest } from "@angular/common/http";
import { Injectable } from "@angular/core";
import { Router } from "@angular/router";
import { from, Observable, switchMap } from "rxjs";
import { UserAuthenticateService } from "../service/user-authenticate.service";
import { LoginService } from './../service/login.service';

const rotasNaoVerificadas = ['/', '/signup'];
@Injectable({ providedIn: 'root' })
export class AtualizarTokenInterceptor implements HttpInterceptor {
	constructor(private userAuthService: UserAuthenticateService,
		private loginService: LoginService,
		private router: Router) { }

	intercept(req: HttpRequest<any>, next: HttpHandler): Observable<HttpEvent<any>> {
		const usuarioEstaAutenticado = this.userAuthService.verificaSeOUsuarioEstaAuthenticado();
		console.log(`Usuario autenticado: ${!usuarioEstaAutenticado}`);
		try {
			const urlAtual = this.router.url.split('?')[0];
			const essaRotaEhProtegida = !rotasNaoVerificadas.includes(urlAtual);
			console.log(`rota protegida: ${essaRotaEhProtegida}`)
			if (!usuarioEstaAutenticado && essaRotaEhProtegida) {
				return from(this.atualizarRequestComTokenNovo(req))
				.pipe(switchMap(reqClone => next.handle(reqClone)));
			}
		} catch (e) { console.log(e)}
		return next.handle(req);

	}

	private async atualizarRequestComTokenNovo(req: HttpRequest<any>) {
		const novaRequestComTokenAtualizado = req.clone();
		const loginDto = await this.loginService.atualizarToken();
		this.userAuthService.salvarLoginDto(loginDto);
		novaRequestComTokenAtualizado.headers.set('authorization', `${loginDto.authType} ${loginDto.token}`);
		return novaRequestComTokenAtualizado;
	}

}
